let landingPage=document.querySelector(".landing-page"),imagesArray=Array();for(let e=0;e<6;e++)imagesArray[e]=e+1+".jpg";let duration=5e3;setInterval(()=>{var e=Math.floor(Math.random()*imagesArray.length);landingPage.style.backgroundImage='url("imgs/landing/'+imagesArray[e]+'")'},duration);let menuButtonShow=document.querySelector(".links-menu i"),menuButtonHide=document.querySelector(".links .icon i"),menu=document.querySelector(".links");menuButtonShow.onclick=function(){menu.style.right=0},menuButtonHide.onclick=function(){menu.style.right="-"+menu.offsetWidth+"px"};